import logo from './logo.svg';
import './App.css';
import Voting from './Voting'
import { Component } from 'react';



class App extends Component {

  constructor(props){
    super(props);
    this.state= {currentTime: "no time set yet" }
  }

  componentDidMount(){
    this.timeInitializer();
    this.timeUpdater();
  }

  timeInitializer(){
    // mydate.toLocaleTimeString("en-US", {timeZone: "CET"})

    let datenow = new Date(Date.UTC());

    let hours = datenow.getHours();
    let minutes = datenow.getMinutes();
    let seconds = datenow.getSeconds();

    this.setState({hours: hours, minutes: minutes, seconds: seconds})
  }

  timeUpdater = () => {
    setInterval(()=> {
      this.timeInitializer();
    }, 1000);
  }

  render(){
    return (
      <div className="App">
         <div className="header">
          <div className="timebox">{this.state.hours}: 
                                    {this.state.minutes}: 
                                    {this.state.seconds} 
          </div>
         </div>
         <div className="selector-box-container">
           <div className="selector-box">
            <Voting />
           </div>
         </div>
      </div>
    );
  }
}

export default App;
