      //  seconds = datenow.getSeconds();

    }, 1000);
  }

  render(){
    return (
      <div className="App">
         <div className="timebox">{this.state.hours} : 
                                  {this.state.minutes} : 
                                  {this.state.seconds} 
         </div>
         <Voting />
      </div>
    );
  }
}

export default App;
