import logo from './logo.svg';
import './App.css';
import Voting from './Voting'
import { Component } from 'react';



class App extends Component {

  constructor(props){
    super(props);
    this.state= {hours: 0, minutes: 0, seconds: 0 }
  }

  componentDidMount(){
    this.timeInitializer();
    this.timeUpdater();
  }

  timeInitializer(){
    let datenow = new Date();

    let hours = datenow.getHours();
    let minutes = datenow.getMinutes();
    let seconds = datenow.getSeconds();

    this.setState({hours: hours, minutes: minutes, seconds: seconds})
  }

  timeUpdater = () => {
    setInterval(()=> {
      this.timeInitializer();
    }, 1000);
  }

  render(){
    return (
      <div className="App">
         <div className="header">
          <div className="timebox">{this.state.hours}: 
                                    {this.state.minutes}: 
                                    {this.state.seconds} 
          </div>
         </div>
         <Voting />
      </div>
    );
  }
}

export default App;
